document.addEventListener('DOMContentLoaded',function(){console.log('Hi and welcome to Jugend hackt. I will be your guide!');document.querySelector('body').classList.add('js');let toc=document.querySelector('.c-toc');if(toc){new Toc().init(toc);}});function Toc(selector){this.init=function(obj){this.el=obj;this.nav=this.el.querySelector('.c-toc-nav');if(this.el){this.nav.querySelectorAll('a').forEach(x=>x.addEventListener('click',this.toggleEvent.bind(this)));let firstElementId=this.nav.querySelector('li:first-of-type a').attributes['href']['nodeValue'];this.activateSingle(firstElementId);}};this.toggleEvent=function(ev){ev.preventDefault();this.deactivateAll();let activeId=ev.target.attributes['href']['nodeValue'];this.activateSingle(activeId);};this.deactivateAll=function(){[].concat(...this.el.querySelectorAll('.c-toc-nav a')).concat(...this.el.querySelectorAll('.c-toc-content section')).forEach(x=>x.classList.remove('is-active'));};this.activateSingle=function(id){let navItem=this.el.querySelector(`[href="${id}"]`);let contentItem=this.el.querySelector(id);[navItem,contentItem].forEach(x=>x.classList.add('is-active'));};}